import { useState } from "react";

export default function App() {
  const [noCount, setNoCount] = useState(0);
  const [yesPressed, setYesPressed] = useState(false);
  const [noButtonPosition, setNoButtonPosition] = useState({ top: "50%", left: "50%" });

  const yesButtonSize = noCount * 23 + 16;

  const noImages = [
    "/img/cat-1.jpg", "/img/cat-2.jpg", "/img/cat-3.jpg", "/img/cat-4.jpg",
    "/img/cat-5.jpg", "/img/cat-6.jpg", "/img/cat-7.jpg", "/img/cat-8.jpg",
    "/img/cat-9.jpg", "/img/cat-10.jpg", "/img/cat-11.jpg", "/img/cat-12.jpg",
    "/img/cat-13.jpg", "/img/cat-14.jpg"
  ];

  const handleNoClick = () => {
    if (noCount < noImages.length - 1) {
      setNoCount((prev) => prev + 1);
      setYesPressed(false);
      setNoButtonPosition({
        top: `${Math.random() * 90}%`, // Move anywhere in viewport height
        left: `${Math.random() * 90}%` // Move anywhere in viewport width
      });
    }
  };

  const getNoButtonText = () => {
    const phrases = [
      "No", "Bruh 👁️👄👁️", "Get a load of this guy", "为什么 😿", "My therapist will hear about this",
      "I hope your pillow is always warm at night", "I see how it is.", "I thought we were friends",
      "Oh you think you're funny?", "Your phone battery will die at 1% today", "I hope your socks get wet today",
      "I can't believe you've done this", "💔💔💔💔☹️☹️☹️☹️", "k wtv bro."
    ];
    return phrases[Math.min(noCount, phrases.length - 1)];
  };

  return (
    <div className="relative flex flex-col items-center justify-center h-screen overflow-hidden">
      {!yesPressed ? (
        <>
          <img className="h-[200px]" src={noImages[noCount]} alt="Changing Cat Image" />
          <h1 className="text-4xl my-4">Will you be my Valentine?</h1>
        </>
      ) : (
        <>
          <img
            src={noCount < noImages.length - 1 
              ? "https://media1.giphy.com/media/v1.Y2lkPTc5MGI3NjExZHppMTBqYjA3aTZuanQ4cnVkbDk2bXZvc3MyY256aHB5d3h6bTF6biZlcD12MV9pbnRlcm5naWZfYnlfaWQmY3Q9Zw/qiMbLh4WHEZyw/giphy.gif" 
              : "/img/cat-yes.jpg"}
            alt="Valentine Response"
            style={{ height: "500px", width: "auto", borderRadius: "10px" }}
          />
          <div className="text-4xl font-bold my-4">
            {noCount < noImages.length - 1 ? "You thought bro" : "Ok yay!!!"}
          </div>
        </>
      )}

      {!(yesPressed && noCount === noImages.length - 1) && (
        <>
          {/* Container to keep the buttons centered */}
          <div className="flex justify-center items-center mt-4">
            <button
              className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mr-4"
              style={{ fontSize: yesButtonSize, paddingLeft: yesButtonSize, paddingRight: yesButtonSize }}
              onClick={() => setYesPressed(true)}
            >
              Yes
            </button>
          </div>

          {/* No Button moves around freely on the screen */}
          {noCount < noImages.length - 1 && (
            <button
              onClick={handleNoClick}
              className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded absolute"
              style={{
                position: "absolute",
                top: noButtonPosition.top,
                left: noButtonPosition.left,
                transition: "top 0.3s ease, left 0.3s ease"
              }}
            >
              {getNoButtonText()}
            </button>
          )}
        </>
      )}
    </div>
  );
}
