import { useState } from "react";

export default function App() {
  const [noCount, setNoCount] = useState(0);
  const [yesPressed, setYesPressed] = useState(false);

  // List of images to cycle through when clicking "No"
  const noImages = [
    "/img/cat-1.jpg",
    "/img/cat-2.jpg",
    "/img/cat-3.jpg",
    "/img/cat-4.jpg",
    "/img/cat-5.jpg",
    "/img/cat-6.jpg",
    "/img/cat-7.jpg",
    "/img/cat-8.jpg",
    "/img/cat-9.jpg",
    "/img/cat-10.jpg",
    "/img/cat-11.jpg",
    "/img/cat-12.jpg",
    "/img/cat-13.jpg",
    "/img/cat-14.jpg",
  ];

  // Function to handle "No" button clicks
  const handleNoClick = () => {
    setNoCount((prev) => Math.min(prev + 1, noImages.length - 1)); // Stop at last image
    setYesPressed(false); // Reset GIF mode so images come back
  };

  // Function to get dynamic "No" button text
  const getNoButtonText = () => {
    const phrases = [
      "No",
      "Bruh üëÅÔ∏èüëÑüëÅÔ∏è",
      "Get a load of this guy",
      "‰∏∫‰ªÄ‰πà üòø",
      "My therapist will hear about this",
      "I hope your pillow is always warm at night",
      "I see how it is.",
      "I thought we were friends",
      "Oh you think you're funny?",
      "Your phone battery will die at 1% today",
      "I hope your socks get wet today",
      "I can't believe you've done this",
      "üíîüíîüíîüíî‚òπÔ∏è‚òπÔ∏è‚òπÔ∏è‚òπÔ∏è",
      "k wtv bro.",
    ];

    return phrases[Math.min(noCount, phrases.length - 1)];
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen -mt-16 relative">
      {/* Show cat images unless final "Yes" is clicked */}
      {!(yesPressed && noCount === noImages.length - 1) && (
        <>
          <img
            className="h-[200px] absolute top-[100px] left-1/2 transform -translate-x-1/2"
            src={noImages[noCount]}
            alt="Changing Cat Image"
          />
          <h1 className="text-4xl my-4 absolute top-[320px] left-1/2 transform -translate-x-1/2">
            Will you be my Valentine?
          </h1>
        </>
      )}

      {/* Show GIF when "Yes" is clicked, unless it's the final "Yes" */}
      {yesPressed && noCount < noImages.length - 1 && (
        <>
          <img
            src="https://media1.giphy.com/media/v1.Y2lkPTc5MGI3NjExZHppMTBqYjA3aTZuanQ4cnVkbDk2bXZvc3MyY256aHB5d3h6bTF6biZlcD12MV9pbnRlcm5hbF9naWZfYnlfaWQmY3Q9Zw/qiMbLh4WHEZyw/giphy.gif"
            alt="Valentine Response"
            className="h-[500px] w-auto absolute top-[400px] left-1/2 transform -translate-x-1/2"
          />
          <div className="text-4xl font-bold my-4 absolute top-[920px] left-1/2 transform -translate-x-1/2">
            You thought bro
          </div>
        </>
      )}

      {/* Show the final "Yes" image when all 14 "No" images have passed */}
      {yesPressed && noCount === noImages.length - 1 && (
        <>
          <img
            src="/img/cat-yes.jpg"
            alt="Final Valentine Response"
            className="h-[500px] w-auto absolute top-[400px] left-1/2 transform -translate-x-1/2"
          />
          <div className="text-4xl font-bold my-4 absolute top-[920px] left-1/2 transform -translate-x-1/2">
            Ok yay!!!
          </div>
        </>
      )}

      {/* Keep buttons static, only disappear on final "Yes" */}
      {!(yesPressed && noCount === noImages.length - 1) && (
        <div className="relative mt-[550px]">
          <button
            className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mr-4"
            onClick={() => setYesPressed(true)}
          >
            Yes
          </button>
          <button
            onClick={handleNoClick}
            className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
          >
            {getNoButtonText()}
          </button>
        </div>
      )}
    </div>
  );
}
