import { useState } from "react";

export default function App() {
  const [noCount, setNoCount] = useState(0);
  const [yesPressed, setYesPressed] = useState(false);
  const [noButtonPosition, setNoButtonPosition] = useState({ top: "50%", left: "50%" });
  const yesButtonSize = noCount * 35 + 25;
  const yesButtonWidth = `${Math.min(95 + noCount * 2.5, 100)}%`;
  const yesButtonHeight = `${Math.min(20 + noCount * 2.5, 50)}vh`;

  // List of images to cycle through when clicking "No"
  const noImages = [
    "/img/cat-1.jpg",
    "/img/cat-2.jpg",
    "/img/cat-3.jpg",
    "/img/cat-4.jpg",
    "/img/cat-5.jpg",
    "/img/cat-6.jpg",
    "/img/cat-7.jpg",
    "/img/cat-8.jpg",
    "/img/cat-9.jpg",
    "/img/cat-10.jpg",
    "/img/cat-11.jpg",
    "/img/cat-12.jpg",
    "/img/cat-13.jpg",
    "/img/cat-14.jpg",
  ];

  const handleNoClick = () => {
    setNoCount((prev) => Math.min(prev + 1, noImages.length - 1)); // Stop at last image
    setYesPressed(false); // Reset GIF mode so images come back

    // Randomize the button position unless the GIF is on screen
    if (noCount < noImages.length - 1) {
      const randomTop = Math.floor(Math.random() * 60) + 20 + "%";
      const randomLeft = Math.floor(Math.random() * 60) + 20 + "%";
      setNoButtonPosition({ top: randomTop, left: randomLeft });
    } else {
      setNoButtonPosition({ top: "50%", left: "50%" });
    }
  };

  const getNoButtonText = () => {
    const phrases = [
      "No",
      "Bruh 👁️👄👁️",
      "Get a load of this guy",
      "为什么 😿",
      "My therapist will hear about this",
      "I hope your pillow is always warm at night",
      "I see how it is.",
      "I thought we were friends",
      "Oh you think you're funny?",
      "Your phone battery will die at 1% today",
      "I hope your socks get wet today",
      "I can't believe you've done this",
      "💔💔💔💔☹️☹️☹️☹️",
      "k wtv bro.",
    ];

    return phrases[Math.min(noCount, phrases.length - 1)];
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen w-screen -mt-16 relative">
      {!yesPressed ? (
        <>
          <img className="h-[200px]" src={noImages[noCount]} alt="Changing Cat Image" />
          <h1 className="text-4xl my-4">Will you be my Valentine?</h1>
        </>
      ) : (
        <>
          <img
            src={noCount < noImages.length - 1 
              ? "https://media1.giphy.com/media/v1.Y2lkPTc5MGI3NjExZHppMTBqYjA3aTZuanQ4cnVkbDk2bXZvc3MyY256aHB5d3h6bTF6biZlcD12MV9pbnRlcm5hbF9naWZfYnlfaWQmY3Q9Zw/qiMbLh4WHEZyw/giphy.gif" 
              : "/img/cat-yes.jpg"}
            alt="Valentine Response"
            className="h-[500px] w-auto rounded-lg"
          />
          <div className="text-4xl font-bold my-4">
            {noCount < noImages.length - 1 ? "You thought bro" : "Ok yay!!!"}
          </div>
        </>
      )}

      {!(yesPressed && noCount === noImages.length - 1) && (
        <div className="w-full flex justify-center">
          <button
            className="bg-green-500 hover:bg-green-700 text-white font-bold rounded flex items-center justify-center"
            style={{ fontSize: yesButtonSize, width: yesButtonWidth, height: yesButtonHeight }}
            onClick={() => setYesPressed(true)}
          >
            Yes
          </button>
        </div>
      )}

      <button
        onClick={handleNoClick}
        className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded absolute"
        style={{ top: noButtonPosition.top, left: noButtonPosition.left, transform: "translate(-50%, -50%)" }}
      >
        {getNoButtonText()}
      </button>
    </div>
  );
}
