import { useState } from "react";

export default function App() {
  const [noCount, setNoCount] = useState(0);
  const [yesPressed, setYesPressed] = useState(false);
  const yesButtonSize = noCount * 30 + 16;

  // List of images to cycle through when clicking "No"
  const noImages = [
    "/img/cat-1.jpg",
    "/img/cat-2.jpg",
    "/img/cat-3.jpg",
    "/img/cat-4.jpg",
    "/img/cat-5.jpg",
    "/img/cat-6.jpg",
    "/img/cat-7.jpg",
    "/img/cat-8.jpg",
    "/img/cat-9.jpg",
    "/img/cat-10.jpg",
    "/img/cat-11.jpg",
    "/img/cat-12.jpg",
    "/img/cat-13.jpg",
    "/img/cat-14.jpg",
  ];

  // Function to handle "No" button clicks
  const handleNoClick = () => {
    setNoCount((prev) => Math.min(prev + 1, noImages.length - 1)); // Stop at last image
    setYesPressed(false); // Reset GIF mode so images come back
  };

  // Function to get dynamic "No" button text
  const getNoButtonText = () => {
    const phrases = [
      "No",
      "Bruh üëÅÔ∏èüëÑüëÅÔ∏è",
      "Get a load of this guy",
      "‰∏∫‰ªÄ‰πà üòø",
      "My therapist will hear about this",
      "I hope your pillow is always warm at night",
      "I see how it is.",
      "I thought we were friends",
      "Oh you think you're funny?",
      "Your phone battery will die at 1% today",
      "I hope your socks get wet today",
      "I can't believe you've done this",
      "üíîüíîüíîüíî‚òπÔ∏è‚òπÔ∏è‚òπÔ∏è‚òπÔ∏è",
      "k wtv bro.",
    ];

    return phrases[Math.min(noCount, phrases.length - 1)];
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen -mt-16">
      {/* Show images unless yesPressed is true */}
      {!yesPressed ? (
        <>
          {/* Show different images on each "No" click */}
          <img className="h-[200px]" src={noImages[noCount]} alt="Changing Cat Image" />
          <h1 className="text-4xl my-4">Will you be my Valentine?</h1>
        </>
      ) : (
        <>
          {/* Show GIF for every "Yes" click except the final one */}
          <img
            src={noCount < noImages.length - 1 
              ? "https://media1.giphy.com/media/v1.Y2lkPTc5MGI3NjExZHppMTBqYjA3aTZuanQ4cnVkbDk2bXZvc3MyY256aHB5d3h6bTF6biZlcD12MV9pbnRlcm5hbF9naWZfYnlfaWQmY3Q9Zw/qiMbLh4WHEZyw/giphy.gif" 
              : "/img/cat-yes.jpg"}
            alt="Valentine Response"
            style={{ height: "500px", width: "auto", borderRadius: "10px" }}
          />
          <div className="text-4xl font-bold my-4">
            {noCount < noImages.length - 1 ? "You thought bro" : "Ok yay!!!"}
          </div>
        </>
      )}

      {/* Keep the buttons unless it's the final "Yes" */}
      {!(yesPressed && noCount === noImages.length - 1) && (
        <div>
          <button
            className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mr-4"
            style={{ fontSize: yesButtonSize }}
            onClick={() => setYesPressed(true)}
          >
            Yes
          </button>
          <button
            onClick={handleNoClick}
            className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
          >
            {getNoButtonText()}
          </button>
        </div>
      )}
    </div>
  );
}
